function createfigure(data1, region, bin)
%CREATEFIGURE(data1)
%  DATA1:  histogram data

%  Auto-generated by MATLAB on 02-Jun-2020 15:06:38

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create histogram
H = histogram(data1,'Parent',axes1,...
    'FaceColor',[0.635294117647059 0.0784313725490196 0.184313725490196],...
    'BinMethod','auto');

% Create ylabel
ylabel({'Raw count'});

% Create xlabel
xlabel({'Diff Score'});

% Create title
title({strcat(region,': Distribution of Diff Scores')});

% Highlight bin with true value
i = bin; 
x = H.BinEdges(i:i+1); x = [x fliplr(x)]; y = [0 0 repmat(H.Values(i), 1, 2)]; 
h_patch = patch(x, y, 'blue');

box(axes1,'on');
